import{Router}from"express";import csrfProtection from"../mid-security/csrf.create.token.js";import csrfErrorHandler from"../mid-security/csrf.error.handler.js";const csrfRouter=Router();csrfRouter.get("/csrf-token",csrfProtection,csrfErrorHandler,(r,o)=>{const t=r.csrfToken();o.status(200).json({csrf:t})});export default csrfRouter;