import{handleErrors}from"../../mid-functions/error.handler";describe("handleErrors function testing",()=>{test("returns email error message when email is incorrect",()=>{const e=handleErrors({message:"incorrect email"});expect(e).toEqual({email:"That email is not registered yet!",password:""})}),test("returns password error message when password is incorrect",()=>{const e=handleErrors({message:"incorrect password"});expect(e).toEqual({email:"",password:"Invalid password!"})}),test("returns duplicate email error message when code is 11000",()=>{const e=handleErrors({message:"",code:11e3});expect(e).toEqual({email:"That email is already used!",password:""})}),test("returns validation messages from user validation errors",()=>{const e=handleErrors({message:"user validation failed",errors:{email:{properties:{path:"email",message:"Invalid email format"}},password:{properties:{path:"password",message:"Password must be at least 6 characters"}}}});expect(e).toEqual({email:"Invalid email format",password:"Password must be at least 6 characters"})}),test("returns empty error messages when unknown error occurs",()=>{const e=handleErrors({message:"some unknown error"});expect(e).toEqual({email:"",password:""})})});